import React from 'react';
import './TableProps.scss';

const Red = props => <span style={{color: "red"}} {...props} />;

const TableProps = ({propDefinitions}) => {
  const igonoreProps = ['classes', 'sheet', 'itemRef'];
  const props = propDefinitions.map(
    ({property, propType, required, description, defaultValue}) => {
      if(!igonoreProps.includes(property)) {
        return (
          <tr key={property}>
            <td className="blue">
              {property}
              {required ? <Red>*</Red> : null}
            </td>
            <td className="red">{propType.name || propType}</td>
            <td>{typeof defaultValue === 'boolean' ? defaultValue.toString() : defaultValue}</td>
            <td>{description}</td>
          </tr>
        );
      }
    }
  );

  return (
    <table className="tb">
      <thead>
      <tr>
        <th>name</th>
        <th>type</th>
        <th>default</th>
        <th>description</th>
      </tr>
      </thead>
      <tbody>{props}</tbody>
    </table>
  );
};

export default TableProps;
