import React, {Fragment} from 'react';
import Utils from '../../../../../src/components/utils/utils';
import IconList from '../../../../../stories/helpers/iconList.js';
import {Icon, Label} from '../../../../../src/components';
import Header3 from '../../../../components/Header3';
import './Icons.scss';

const sizes = ['micro', 'small', 'medium', 'large', 'xlarge'].reverse();

const Icons = () => (
  <div className="brandbook__icons">
    {sizes.map(size => (
      <Fragment key={Utils.random()}>
        <Header3 title={size}/>
        <div className="brandbook__icons__row">
          {IconList.map(
            icon =>
              icon !== null && (
                <div key={Utils.random()} className="brandbook__icons__cell">
                  <Label className="brandbook__icons__label" value={icon} fontSize="xsmall"/>
                  <Icon type={icon} size={size} color="#3B89D6"/>
                </div>
              ),
          )}
        </div>
      </Fragment>
    ))}
  </div>
);

export default Icons;
