import React from 'react';
import renderer from 'react-test-renderer';
import ThemeProvider from '../ThemeProvider';
import Avatar from './Avatar';
import theme from '../themes/corezoid_light';

test('Avatar simple test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Avatar />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});


test('Avatar with src test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Avatar src="https://www.corezoid.com/avatars/59439.jpg"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Avatar size && bgColor test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Avatar size="small" bgColor="#000"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Avatar size && textColor test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Avatar size="large" textColor="#000"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Avatar size && name test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Avatar size="xlarge" name="Full Name"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});
