import React from 'react';
import renderer from 'react-test-renderer';
import ThemeProvider from '../ThemeProvider';
import Chip from './Chip';
import theme from '../themes/corezoid_light';

test('Chip simple test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip type & icon test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip type="icon" icon="time" />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip type & name test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip type="avatar" name="Full Name" />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip type & avatar test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip type="avatar" src="https://www.corezoid.com/avatars/59439.jpg"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip label test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip lable="Label" />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip color test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip textColor="#000" bgColor="#000" />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Chip is close icon test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Chip closeIcon={false} />
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});
