import EmojiMart from './EmojiMart.style';

export default (theme) => {
  const EmojiMartTheme = EmojiMart(theme);
  return Object.assign({}, EmojiMartTheme, {
    emoji: {
      display: 'inline-block',
      position: 'relative',
      cursor: 'pointer',
      '&.hidden': {
        display: 'none'
      },
      '&.disabled': {
        opacity: theme.disabledOpacity,
        cursor: 'default'
      }
    },
    picker: {
      zIndex: 10,
      position: 'absolute',
      '&.hidden': {
        display: 'none'
      }
    }
  });
};
