import React from 'react';
import renderer from 'react-test-renderer';
import ThemeProvider from '../ThemeProvider';
import Icon from './Icon';
import theme from '../themes/corezoid_light';

test('Icon simple test', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Icon error display', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy" error={true}/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Disabled icon', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy" visibility="disabled"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Hidden icon', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy" visibility="hidden"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Custom color', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy" color="#cccccc"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});

test('Icon size', () => {
  const component = renderer.create(
    <ThemeProvider theme={theme}>
      <Icon type="copy" size="xlarge"/>
    </ThemeProvider>
  );
  const tree = component.toJSON();
  expect(tree).toMatchSnapshot();
});
