import React from 'react';
import {storiesOf} from '@storybook/react';
import {text, select, color} from '@storybook/addon-knobs/react';
import Avatar from '../src/components/Avatar';
import Icon from '../src/components/Icon';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';
import Badge from '../src/components/Badge/Badge';

const stories = storiesOf('Avatar', module);
infoDecorator(stories);
themeDecorator(stories);

const size = ['small', 'medium', 'large', 'xlarge', 'xxlarge'];

stories
  .add('with label', () => (
    <Avatar
      size={select('Size', size, 'medium')}
      label={text('Label', 'Full Name')}
      textColor={color('textColor')}
      bgColor={color('bgColor')}
    />
  ))
  .add('with src', () => (
    <Avatar
      size={select('Size', size, 'medium')}
      label={text('Label', 'Full Name')}
      src={text('src', '/test-imgd.jpg')}
      bgColor={color('bgColor')}
    />
  ))
  .add('with children', () => (
    <Avatar size={select('Size', size, 'medium')} bgColor={color('bgColor')}>
      <Icon type="document" color="white" size="medium"/>
    </Avatar>
  ))
  .add('with children & src', () => (
    <Avatar
      size={select('Size', size, 'medium')}
      src={text('src', '/test-img.jpg')}
      bgColor={color('bgColor')}
    >
      <Icon type="document" color="white" size="medium"/>
    </Avatar>
  ))
  .add('with online status', () => (
    <Avatar
      size={select('Size', size, 'medium')}
      label={text('Label', 'Full Name')}
      badge={
        <Badge
          size={select('Badge Size', size, 'medium')}
          bgColor={color('Badge bg color')}
          borderColor={color('Badge border color')}
        />
      }
    />
  ));
