import React from 'react';
import {storiesOf} from '@storybook/react';
import {text, select, boolean} from '@storybook/addon-knobs/react';
import Button from '../src/components/Button';
import {MenuItem} from '../src/components/Menu';
import Divider from '../src/components/Divider';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';
import iconsList from './helpers/iconList';

const stories = storiesOf('Button', module);
infoDecorator(stories);
themeDecorator(stories);

const types = ['default', 'text', 'secondary'];
const size = ['small', 'smallplus', 'medium', 'large'];
const visibility = ['visible', 'disabled', 'hidden'];

stories
  .add('default', () => (
    <Button
      type={select('Type', types, 'default')}
      size={select('Size', size, 'medium')}
      label={text('Label', 'Button')}
      visibility={select('Visibility', visibility, 'visible')}
      error={boolean('Error', false)}
      rounded={boolean('Rounded', false)}
      icon={select('Icon', iconsList, 'search')}
      iconAfter={select('IconAfter', iconsList, 'mail')}
    />
  ))
  .add('button with menu', () => (
    <Button
      type={select('Type', types, 'default')}
      size={select('Size', size, 'medium')}
      label={text('Label', 'Button with menu')}
      visibility={select('Visibility', visibility, 'visible')}
      error={boolean('Error', false)}
      rounded={boolean('Rounded', false)}
      iconAfter={select('IconAfter', iconsList, 'arrow')}
    >
      <MenuItem label={'MenuItem1'} value={'1'}/>
      <MenuItem label={'MenuItem2'} value={'2'} leftIcon="dog" rightIcon="check"/>
      <Divider/>
      <MenuItem label={'MenuItem3'} value={'3'}/>
    </Button>
  ));
