import React from 'react';
import {storiesOf} from '@storybook/react';
import {boolean, select, text} from '@storybook/addon-knobs/react';
import Checkbox from '../src/components/Checkbox';
import Chip from '../src/components/Chip';
import Label from '../src/components/Label';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';

const stories = storiesOf('Checkbox', module);
infoDecorator(stories);
themeDecorator(stories);

stories
  .add('default', () => (
    <Checkbox
      visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
      value={boolean('Value', true)}
      error={boolean('Error', false)}
      helperText={text('helperText')}
    >
      Checkbox
    </Checkbox>
  ))
  .add('with Chip children', () => (
    <Checkbox
      visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
      value={boolean('Value', true)}
      error={boolean('Error', false)}
      helperText={text('helperText')}
    >
      <Chip type="rectangular" size="medium" label="Checkbox" closeIcon={false}/>
    </Checkbox>
  ))
  .add('multiple with helperText', () => (
    <div>
      <Checkbox
        visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
        value={boolean('Value', true)}
        error={boolean('Error', false)}
        helperText={text('helperText', 'Some helper description')}
      >
        <Label value="Checkbox1"/>
      </Checkbox>
      <Checkbox>
        <Label value="Checkbox2"/>
      </Checkbox>
    </div>
  ));
