import React from 'react';
import {storiesOf} from '@storybook/react';
import {select, text, boolean} from '@storybook/addon-knobs/react';
import Chip from '../src/components/Chip';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';
import iconsList from './helpers/iconList';

const stories = storiesOf('Chip', module);
const types = ['rounded', 'rectangular'];
const sizes = ['small', 'medium', 'large'];
const fontSize = ['small', 'medium'];
infoDecorator(stories);
themeDecorator(stories);

stories.add('default', () => (
  <Chip
    label={text('Label', 'full name')}
    src={text('src', 'https://cdn.goandroid.co.in/wp-content/uploads/2017/07/www.androidworld.itwp-contentuploads201701Google-App-a9443e2bbb3aaf921f4f58cea2bbaf60a9deb7b6.jpg')}
    icon={select('Icon', iconsList, 'search')}
    closeIcon={boolean('closeIcon', true)}
    type={select('Type', types, 'rounded')}
    size={select('Size', sizes, 'medium')}
    fontSize={select('Font size', fontSize, 'medium')}
  />
));
