import React from 'react';
import {storiesOf} from '@storybook/react';
import {select, text, boolean, color} from '@storybook/addon-knobs/react';
import Label from '../src/components/Label';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';

const stories = storiesOf('Label', module);
infoDecorator(stories);
themeDecorator(stories);

const size = ['xsmall', 'small', 'medium', 'large', 'xlarge'];
const weight = ['normal', 'semibold'];
const visibility = ['visible', 'disabled', 'hidden'];

stories
  .add('default',
    () => (
      <Label
        fontSize={select('fontSize', size, 'medium')}
        fontWeight={select('fontWeight', weight, 'normal')}
        visibility={select('Visibility', visibility, 'visible')}
        value={text('Value', 'label value')}
        textColor={color('textColor')}
        error={boolean('Error', false)}
      />
    )
  );
