import React from 'react';
import {storiesOf} from '@storybook/react';
import {text, select} from '@storybook/addon-knobs/react';
import Radio, {RadioItem} from '../src/components/Radio';
import Banner from '../src/components/Banner';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';

const stories = storiesOf('Radio', module);
infoDecorator(stories);
themeDecorator(stories);

stories.add('default', () => (
  <Radio
    label={text('Label', 'Label text')}
    visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
    value={select('Value', [null, '1', '2', '3'], '1')}
    align={select('Align', ['vertical', 'horizontal'], 'vertical')}
  >
    <RadioItem value={'1'} label="RadioItem1">
      <Banner value="Some additional child"/>
    </RadioItem>
    <RadioItem value={'2'} label="DisabledItem" visibility="disabled"/>
    <RadioItem value={'3'} label="RadioItem3"/>
  </Radio>
));

stories.add('radioItem', () => (
  <RadioItem
    label={text('Label', 'Label text')}
    value={text('Value', '1')}
    visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
  />
));
