import React from 'react';
import {storiesOf} from '@storybook/react';
import {text, boolean, select} from '@storybook/addon-knobs/react';
import Select from '../src/components/Select';
import {MenuItem} from '../src/components/Menu';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';

const stories = storiesOf('Select', module);
infoDecorator(stories);
themeDecorator(stories);

stories
  .add('default',
    () => (
      <div>
        <Select
          id="select"
          label={text('Label', 'Label')}
          value={text('Value', '')}
          openType={select('openType', ['', 'top'], '')}
          error={boolean('Error', false)}
          helperText={text('helperText', 'Empty value is not allowed')}
          visibility={select('Visibility', ['visible', 'disabled', 'hidden'])}
          bordered={false}
        >
          <MenuItem
            label={'MenuItem1'}
            value="1"
          />
          <MenuItem
            label={'MenuItem2'}
            value="2"
          />
          <MenuItem
            label={'MenuItem3'}
            value="3"
          />
          <MenuItem
            label={'MenuItem4'}
            value="4"
          />
          <MenuItem
            label={'MenuItem5'}
            value="5"
          />
        </Select>
      </div>
    )
  );

stories
  .add('bordered',
    () => (
      <div>
        <Select
          id="select2"
          label={text('Label', '')}
          value={text('Value', '')}
          openType={select('Type', ['', 'top'], '')}
          error={boolean('Error', false)}
          helperText={text('helperText', '')}
          visibility={select('Visibility', ['visible', 'disabled', 'hidden'])}
          bordered={true}
        >
          <MenuItem
            label={'MenuItem1'}
            value="1"
          />
          <MenuItem
            label={'MenuItem2'}
            value="2"
          />
          <MenuItem
            label={'MenuItem3'}
            value="3"
          />
          <MenuItem
            label={'MenuItem4'}
            value="4"
          />
          <MenuItem
            label={'MenuItem5'}
            value="5"
          />
        </Select>
      </div>
    )
  );
