import React from 'react';
import {storiesOf} from '@storybook/react';
import {boolean, select, text} from '@storybook/addon-knobs/react';
import Tab, {TabItem} from '../src/components/Tab';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';
import iconsList from './helpers/iconList';

const stories = storiesOf('Tab', module);
infoDecorator(stories);
themeDecorator(stories);

stories
  .add('default',
    () => (
      <div style={{width: 400}}>
        <Tab
          size={select('Tab size', ['medium', 'small'])}
          tabItemIndent={select('tabItem indent size', ['small', 'medium', 'large'])}
          value={select('Value', [null, '1', '2', '3', '4'], '1')}
          type={select('Type', ['fixed', 'auto'])}
          underline={boolean('Underline', false)}
          error={boolean('Error', false)}
        >
          <TabItem
            value={'1'}
            label={'TestTab1TestTab1TestTab1TestTab1TestTab1TestTab1Test'}
          />
          <TabItem
            value={'2'}
            label={'Tab2'}
            visibility={'disabled'}
          />
          <TabItem
            value={'3'}
            leftIcon={'eye'}
          />
          <TabItem
            value={'4'}
            label={'Tab4'}
            leftIcon={'eye'}
            rightIcon={'dropdown'}
            rightIconSize={'micro'}
          />
          <TabItem
            value={'5'}
            label={'This tab must be hidden'}
            leftIcon={'eye'}
            visibility={'hidden'}
          />
        </Tab>
      </div>
    )
  );

stories
  .add('tabItem',
    () => (
      <TabItem
        label={text('Label', 'Tab1')}
        icon={select('Icon', iconsList, 'time')}
        value={text('Value', 'test')}
        visibility={select('Visibility', ['visible', 'disabled', 'hidden'])}
        error={boolean('Error', false)}
        className="tabItemClassName"
      />
    )
  );
