import React from 'react';
import {storiesOf} from '@storybook/react';
import {boolean, select, text, number} from '@storybook/addon-knobs/react';
import Upload from '../src/components/Upload';
import Avatar from '../src/components/Avatar';
import Icon from '../src/components/Icon';
import infoDecorator from '../.storybook/customDecorators/infoDecorator';
import themeDecorator from '../.storybook/customDecorators/themeDecorator';

const stories = storiesOf('Upload', module);
infoDecorator(stories);
themeDecorator(stories);
stories
  .add('default',
    () => (
      <Upload
        visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
        preview={boolean('Preview', true)}
        label={text('Label', 'Drop file to attach, or')}
        browseLinkLabel={text('Label', 'browse')}
        error={boolean('Error', false)}
        helperText={text('helperText')}
        multiple={boolean('Multiple', true)}
      />
    )
  )
  .add('avatar',
    () => (
      <Upload
        visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
        label={text('Label', 'Upload file')}
        error={boolean('Error', false)}
        helperText={text('helperText')}
        multiple={boolean('Multiple', false)}
      >
        <Avatar
          label="Test Avatar"
          size="xxlarge"
        />
      </Upload>
    )
  )
  .add('icon',
    () => (
      <div>
        <div id={'container'}/>
        <Upload
          visibility={select('Visibility', ['visible', 'disabled', 'hidden'], 'visible')}
          preview={boolean('Preview', true)}
          label={text('Label', 'Upload file')}
          error={boolean('Error', false)}
          helperText={text('helperText')}
          maxFileSize={number('maxFileSize', 2)}
          multiple={boolean('Multiple', true)}
          onChange={() => {
          }}
          portal={document.getElementById('container')}
        >
          <Icon
            type="attach"
            size="large"
          />
        </Upload>
      </div>
    )
  );
